const ADD_TASK = 'ADD_TASK'
const UPDATE_INPUT = 'UPDATE_INPUT'
const UPDATE_TASK_NAME = 'UPDATE_TASK_NAME'

let state = {
    store:{
        task: [
            {id: 1, text: "Первая задача", done: false},
            {id: 2, text: "Вторая задача", done: false},
            {id: 3, text: "Третья задача", done: false},
        ],
        newTask: ''   
    },

    rerenderTree(){
        console.log(1);
    },

    subscribe(f){
        this.rerenderTree = f
    },

    dispatch(action){

        if(action.type === ADD_TASK){
            let newTask = {
                id: 4,
                text: this.store.newTask
            }
            this.store.task.push(newTask)
            this.rerenderTree()
        }
        if(action.type === UPDATE_INPUT){
            this.store.newTask = action.newTask
            this.rerenderTree()
        }
        if(action.type === UPDATE_TASK_NAME){
            for(let txt in this.store.task){
                if(this.store.task[txt].text === action.nameDoneTask){
                    this.store.task[txt].done = action.flag;
                    console.log(this.store.task[txt])
                    return
                }
            }
            //this.store.task[action.nameDoneTask].done = true;
            this.rerenderTree();
        } 
    }
}

export let addTask = () => {
    return{
        type: ADD_TASK
    }
}

export let updateTask = (text) => {
    return{
        type: UPDATE_INPUT,
        newTask: text
    }
}

export let addClassNameTask = (text, isFlag) => {
    return{
        type: UPDATE_TASK_NAME,
        nameDoneTask: text,
        flag: isFlag
    }
}

export let deleteTask = (text, isFlag) => {
    return{
        type: UPDATE_TASK_NAME,
        nameDoneTask: text,
        flag: isFlag
    }
}

export default state;
