import classes from './Input.module.css'
import { addTask, updateTask } from '../../state/store'
import React from 'react'

const ref = React.createRef()

let updateInput = () => {
    let text = ref.current.value
    updateTask()
}

export function Input(){
    return(
        <div className={classes.wrapper}>
            <div className={classes.wrapper_area}>
                <input className={classes.input} type="text" placeholder='Enter your task' value='' ref={ref}/>
                <button className={classes.button}>+ Add task</button>
            </div>
        </div>
    )
}
