import c from './MyTask.module.css'
import React from 'react'
import { addTask, updateTask } from '../../state/store'

export function MyTask(props){


    const linkCheckbox = React.createRef()
    let active = ''

    let isChecked = () =>{
        active = 'active';
    }

    return(
        <div className={c.task}>
            <label className={c.container}>
                <input className={c.task__checkbox + ' ' + active} type="checkbox" onChange={isChecked} ref={linkCheckbox}/>
                <span className={c.task__choseImg}></span>
            </label>
            <span className={c.nameTask}>{props.text}</span>
        </div>
    )
}
