import c from './MyTask.module.css'
import React, { useState } from 'react'
import { addClassNameTask } from '../../../state/store'

export function MyTask(props){


    const linkCheckbox = React.createRef()
    let active = ''
    const [activeState, setActiveState] = useState(false)

    let isChecked = (event) =>{
        let isFlag = event.target.checked;
        isFlag ? setActiveState = 'active' : active = "";
        props.dispatch(addClassNameTask(props.text, isFlag))
    }

    return(
        <div className={c.task + ' ' + active}>
            <label className={c.container}>
                <input className={c.task__checkbox} type="checkbox" onChange={isChecked} ref={linkCheckbox}/>
                <span className={c.task__choseImg}></span>
            </label>
            <span className={c.nameTask}>{props.text}</span>
        </div>
    )
}
