const ADD_TASK = 'ADD_TASK'
const UPDATE_INPUT = 'UPDATE_INPUT'

let state = {
    store:{
        task: [
            {id: 1, text: "Первая задача"},
            {id: 2, text: "Вторая задача"},
            {id: 3, text: "Третья задача"},
        ],
        newTask: ''   
    },
    dispatch(action){
        if(action === ADD_TASK){
            let newTask = {
                id: 4,
                text: this.store.newTask
            }
        }
        if(action === UPDATE_INPUT){
            this.store.newTask = action.text
        } 
    }
}

let addTask = () => {
    return{
        type: ADD_TASK
    }
}

let updateTask = (text) => {
    return{
        type: UPDATE_INPUT,
        newTask: text
    }
}

export default state;
